    private void jTable2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable2MouseClicked
    
        DefaultTableModel d =(DefaultTableModel)jTable2.getModel(); 
        int selectedRowIndex = jTable2.getSelectedRow();
        
        Flight_ID.setText(d.getValueAt(selectedRowIndex, 0).toString());  
        Airline.setSelectedItem(d.getValueAt(selectedRowIndex, 1).toString());  
        To.setSelectedItem(d.getValueAt(selectedRowIndex, 2).toString());  
        From.setSelectedItem(d.getValueAt(selectedRowIndex, 3).toString());  
        Seats.setText(d.getValueAt(selectedRowIndex, 4).toString());  
        Date.setText(d.getValueAt(selectedRowIndex, 5).toString());  
        Time.setText(d.getValueAt(selectedRowIndex, 6).toString());  
        
        
        
        jButton1.setEnabled(true);
       
    }//GEN-LAST:event_jTable2MouseClicked

